clc;
clear all;
k= 9.1106;
v=-0.1249;
d=0.175;
L=4;
dim=1;
res=0.0001;

cov=0.039;
%  gac=gammainc(L,L*T*x/d*c,'upper') ;
 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
precision_cnt=50;
max_val=0.013;
interval=max_val/precision_cnt;
A={[1+(L*d-k*v)/v;-1/v],[]};
B={[0;-1/v],[]};
cnt=[];
hwpdf=[];
for i=0:(precision_cnt-1);
   
    z=(max_val*(i+1))/2
    x=d*cov/(z*L)
    fox=foxh(x,B,A,res)
    myfox_A=vpa(fox,10);

      spa=power(L,L*dim)*power(d,(L*dim-k*v)/v)*power(z,L*dim);
    spb=power(d,k*v)*gamma(k)*power(cov,L)*gamma(L)*gamma(L-1);
    hwpdf(i+1)=interval*spa*myfox_A/spb;  
   
   
        
    
end;


